Protein_enzyme_interactions.sparql.query:
PREFIX taxon:<http://purl.uniprot.org/taxonomy/>
PREFIX uniprotkb:<http://purl.uniprot.org/uniprot/>
PREFIX rdfs:<http://www.w3.org/2000/01/rdf-schema#>
PREFIX up:<http://purl.uniprot.org/core/>
SELECT DISTINCT ?enzyme ?label WHERE {
VALUES ?protein {uniprotkb:+inputID+}
?protein up:enzyme ?enzyme .
?protein rdfs:label ?label .
?protein up:organism taxon:+taxon+ .
}

get_all_known_interactions.sparql.query:
PREFIX gpml: <http://vocabularies.wikipathways.org/gpml#> PREFIX dcterms: <http://purl.org/dc/terms/> PREFIX dc: <http://purl.org/dc/elements/1.1/> PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT DISTINCT ?interaction ?pathway WHERE {
?pathway a wp:Pathway . ?interaction dcterms:isPartOf ?pathway . ?interaction a wp:Interaction . ?interaction wp:participants <http://identifiers.org/ensembl/ENSG00000125845> . }

inhibited_by.sparql.query:
SELECT * WHERE
{
?interaction a wp:Inhibition ;
wp:source ?source ;
wp:target ?target .
?source dcterms:identifier ?sourceID .
?target dcterms:identifier +inputID+ .
}

inhibits.sparql.query:
SELECT * WHERE
{
?interaction a wp:Inhibition ;
wp:source ?source ;
wp:target ?target .
?source dcterms:identifier +inputID+ .
?target dcterms:identifier ?targetID .
}

is_targeted_by.sparql.query:
SELECT * WHERE
{

?interaction a wp:DirectedInhibition .
?interaction wp:source ?source .
?interaction wp:target ?target .
}

stimulates.sparql.query:
SELECT * WHERE
{

?interaction a wp:Stimulation .
?interaction wp:source ?source .
?interaction wp:target ?target .
}

targets.sparql.query:
SELECT * WHERE
{

?interaction a wp:DirectedInhibition .
?interaction wp:source ?source .
?interaction wp:target ?target .
}

transcribes_translates.sparql.query:
SELECT * WHERE
{

?interaction a wp:TranscriptionTranslation .
?interaction wp:source ?source .
?interaction wp:target ?target .
}

physically_interacts_with.sparql.query:
SELECT ?source ?sourceLabel ?target ?targetLabel ?source_geneId ?source_uniprotId
?target_geneId ?target_uniprotId ?source_pubchemId ?target_pubchemId
WHERE
{
?source
wdt: P129 ?target.
OPTIONAL
{?source
wdt: P351 ?source_geneId}
OPTIONAL
{?source
wdt: P352 ?source_uniprotId}
OPTIONAL
{?source
wdt: P661 ?source_pubchemId}
OPTIONAL
{?target
wdt: P351 ?target_geneId}
OPTIONAL
{?target
wdt: P352 ?target_uniprotId}
OPTIONAL
{?target
wdt: P661 ?target_pubchemId}
SERVICE
wikibase: label
{bd: serviceParam wikibase: language "en"}
}
